mapper.select:
SELECT sub.cs_no, t.type_name, sub.title, sub.created_at, sub.is_answered, sub.total_count FROM ( SELECT cq.*, COUNT(*) OVER() AS total_count, ROW_NUMBER() OVER (ORDER BY cq.created_at DESC) AS rownum FROM CS_QUESTION cq WHERE cq.writer_no = #{memberNo}
AND ( cq.title LIKE CONCAT('%', #{searchWord}, '%') OR cq.content LIKE CONCAT('%', #{searchWord}, '%') )
) sub JOIN TYPE t ON sub.cs_type = t.type_code WHERE sub.rownum BETWEEN #{start} AND #{end}

SELECT sub.cs_no, t.type_name, sub.title, m.nickname, sub.created_at, sub.is_answered, sub.total_count FROM ( SELECT cq.*, COUNT(*) OVER() AS total_count, ROW_NUMBER() OVER (ORDER BY cq.created_at DESC) AS rownum FROM CS_QUESTION cq
(cq.title LIKE CONCAT('%', #{searchWord}, '%') OR cq.content LIKE CONCAT('%', #{searchWord}, '%'))
) sub JOIN TYPE t ON sub.cs_type = t.type_code JOIN MEMBER m ON sub.writer_no = m.member_no WHERE sub.rownum BETWEEN #{start} AND #{end} ORDER BY sub.created_at DESC

mapper.update:
UPDATE CS_QUESTION
SET is_answered = 'Y'
WHERE cs_no = #{csNo}

UPDATE MEMBER
SET modified_at = CURRENT_TIMESTAMP
WHERE member_no = #{targetNo}
