mapper.update:
UPDATE CL
SET chatroom_code = #{ chatroomCode }
WHERE club_no = #{ clubNo }

mapper.select:
SELECT A.club_no, A.club_name, A.introduction, A.max_personnel, B.category_name, C.eup_myeon_dong_name, (SELECT COUNT(*) FROM CL_MEMBER D WHERE D.club_no = A.club_no AND D.join_status = 'Y') AS current_personnel, (SELECT E.saved_name FROM ATTACHMENT E WHERE E.type_code = 'CL' AND E.target_no = A.club_no) AS saved_name FROM CL A LEFT JOIN CL_CATEGORY B ON A.category_code = B.category_code LEFT JOIN LOCATION C ON A.location_code = C.location_code WHERE A.location_code = #{ locationCode }
AND A.category_code = #{ categoryCode }
AND ( A.club_name LIKE CONCAT('%', #{ searchWord }, '%') OR A.introduction LIKE CONCAT('%', #{ searchWord }, '%') )
ORDER BY current_personnel DESC LIMIT #{ startIndex }, #{ pageCount }

SELECT COUNT(*) club_count FROM CL
WHERE location_code = #{ locationCode }

    AND category_code = #{ categoryCode }


    AND (
        club_name LIKE CONCAT('%', #{ searchWord }, '%')
        OR introduction LIKE CONCAT('%', #{ searchWord }, '%')
    )

SELECT join_status
FROM CL_MEMBER
WHERE club_no = #{ clubNo } AND member_no = #{ memberNo }

SELECT master
FROM CL
WHERE club_no = #{ clubNo }

mapper.update:
UPDATE CL
SET
    category_code = #{ category.categoryCode },
    club_name = #{ clubName },
    introduction = #{ introduction }
WHERE club_no = #{ clubNo }

UPDATE ATTACHMENT
SET
    original_name = #{ originalName },
    saved_name = #{ savedName }
WHERE type_code = 'CL' AND target_no = #{ targetNo }

mapper.select:
SELECT *
FROM ATTACHMENT
WHERE type_code = 'CL' AND target_no = #{ clubNo }

SELECT authority
FROM MEMBER
WHERE member_no = #{ memberNo }

SELECT A.log_no, A.club_no, A.title, A.content, A.views, A.created_at, B.nickname AS log_writer_nickname, (SELECT COUNT(*) FROM CL_LOG_LIKE C WHERE C.log_no = A.log_no) AS likes_count, (SELECT COUNT(*) FROM CL_LOG_REPLY D WHERE D.log_no = A.log_no) AS replies_count, (SELECT E.saved_name FROM ATTACHMENT E WHERE E.type_code = 'CL-LOG' AND E.target_no = A.log_no ORDER BY E.file_no LIMIT 1) AS saved_name FROM CL_LOG A LEFT JOIN MEMBER B ON A.writer = B.member_no WHERE A.club_no = #{ clubNo }
AND ( A.title LIKE CONCAT('%', #{ searchWord }, '%') OR A.content LIKE CONCAT('%', #{ searchWord }, '%') )
ORDER BY A.log_no DESC LIMIT #{ startIndex }, #{ pageCount }

SELECT COUNT(*) log_count FROM CL_LOG
WHERE club_no = #{ clubNo }

    AND (
        title LIKE CONCAT('%', #{ searchWord }, '%')
        OR content LIKE CONCAT('%', #{ searchWord }, '%')
    )
