mapper.select:
SELECT sub.message_no, sub.sender_no, m.nickname AS sender_nickname, sub.content, sub.created_at, sub.is_read, sub.total_count FROM ( SELECT msg.*, COUNT(*) OVER() AS total_count, ROW_NUMBER() OVER (ORDER BY CASE WHEN msg.is_read = 'N' THEN 0 ELSE 1 END, msg.created_at DESC ) AS rownum FROM MSG msg WHERE msg.receiver_no = #{memberNo}
AND msg.sender_no IN (SELECT member_no FROM MEMBER WHERE nickname LIKE CONCAT('%', #{searchWord}, '%'))
AND msg.content LIKE CONCAT('%', #{searchWord}, '%')
) sub JOIN MEMBER m ON sub.sender_no = m.member_no WHERE sub.rownum BETWEEN #{start} AND #{end} ORDER BY CASE WHEN sub.is_read = 'N' THEN 0 ELSE 1 END, sub.created_at DESC

SELECT sub.message_no, sub.receiver_no, m.nickname AS receiver_nickname, sub.content, sub.created_at, sub.is_read, sub.total_count FROM ( SELECT msg.*, COUNT(*) OVER() AS total_count, ROW_NUMBER() OVER (ORDER BY msg.created_at DESC) AS rownum FROM MSG msg WHERE msg.sender_no = #{memberNo}
AND msg.receiver_no IN (SELECT member_no FROM MEMBER WHERE nickname LIKE CONCAT('%', #{searchWord}, '%'))
AND msg.content LIKE CONCAT('%', #{searchWord}, '%')
) sub JOIN MEMBER m ON sub.receiver_no = m.member_no WHERE sub.rownum BETWEEN #{start} AND #{end} ORDER BY sub.created_at DESC

SELECT nickname
FROM MEMBER
WHERE member_no = #{receiverNo}

SELECT * FROM MSG
WHERE sender_no = #{senderNo} AND receiver_no = #{receiverNo}
ORDER BY created_at DESC LIMIT 1

mapper.insert:
INSERT INTO MSG_BLOCK (blocker_no, blocked_no)
SELECT #{blockerNo}, #{blockedNo}
FROM MEMBER
WHERE member_no = #{blockedNo}
AND is_deleted = 'N'

mapper.select:
SELECT COUNT(*) > 0
FROM MSG_BLOCK
WHERE blocker_no = #{receiverNo}
AND blocked_no = #{senderNo}
