mapper.update:
UPDATE NOTI
SET is_read = 'Y'
WHERE noti_no = #{notiNo}
AND receiver_no = #{receiverNo}

mapper.delete:
DELETE FROM NOTI
WHERE receiver_no = #{receiverNo}
AND is_read = 'Y'

mapper.update:
UPDATE noti_set
SET is_received = #{isReceived}
WHERE member_no = #{memberNo}
AND noti_set_code = #{notiSetCode}
